{"editors":"0010","html":"<div id="root"></div>","js":"import React from 'react';
import ReactDOM from 'react-dom';
import {
  useKReducer,
  withScope,
  KProvider,
  createReducer,
  createStateReducer,
} from '@k-frame/core';
import {createStore} from 'redux';

const store = createStore((state = {}) => state);

const counterActions = {
  inc: () => ({type: 'increment'}),
};

const counterReducer = createReducer({counter: 0}, [
  createStateReducer(counterActions.inc, s => ({
    ...s,
    counter: s.counter + 1,
  })),
]);

const Counter = withScope(() => {
  const {counter, inc} = useKReducer(
    counterReducer,
    counterActions
  );

  return (
    <button type="button" onClick={inc}>
      Clicked {counter} times
    </button>
  );
});

function App() {
  return (
    <div className="App">
      <Counter scope="c1" />
      <Counter scope="c2" />
    </div>
  );
}

const rootElement = document.getElementById('root');
ReactDOM.render(
  <KProvider store={store}>
    <App />
  </KProvider>,
  rootElement
);
